class Solution:
    # @param A : string
    # @return an integer
    def solve(self, A):

        vowels = ['a','e','i','o','u']
        for i in range(0, len(vowels)):

            vowels.append(chr(ord(vowels[i])-32))

        res = 0
        for i in range(0, len(A)):

            if A[i] in vowels:
                res += (len(A) - i) #* (len(A) -i +1)) / 2
                
        res = res % 10003
        return res


obj = Solution()
A = ""
print(obj.solve(A))
